package com.zhang.mobliesafe;

import com.zhang.mobliesafe.R;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.view.GestureDetector;
import android.view.MotionEvent;
import android.view.View;
import android.view.GestureDetector.SimpleOnGestureListener;
import android.widget.Toast;

public abstract class BaseSetupActivity extends Activity {
	//1.定义一个手势识别器
		private GestureDetector detector;
		@Override
		protected void onCreate(Bundle savedInstanceState) {
			// TODO Auto-generated method stub
			super.onCreate(savedInstanceState);
		
		//2.实例化手势识别器
		detector = new GestureDetector(this, new SimpleOnGestureListener(){
			/**
			 * 手指在上面滑动实现回调	
			 */
			@Override
			public boolean onFling(MotionEvent e1, MotionEvent e2,
					float velocityX, float velocityY) {
				//屏蔽在x滑动很慢的情形
				if(Math.abs(velocityX)<200){
					Toast.makeText(getApplicationContext(), "滑动得太慢了", 0).show();

				}
				
				//屏蔽斜滑这种情况
				if(Math.abs(e2.getRawY()-e1.getRawY())>200){
					Toast.makeText(getApplicationContext(), "不能这样滑动", 0).show();
				}
				
				if((e2.getRawX()-e1.getRawX())>200){
					//从左到右滑动,显示上一个页面
					System.out.println("从左到右滑动,显示上一个页面");
					showPre();
					return true;
				}
				if((e1.getRawX()-e2.getRawX())>200){
					//从右到左滑动,显示下一个页面
					System.out.println("从右到左滑动,显示下一个页面");
					showNext();
					return true;
				}
				return super.onFling(e1, e2, velocityX, velocityY);
			}
		});
		}
		
	public abstract void showNext();
	public abstract void showPre();
	/**
	 * 下一步
	 * @param view
	 */
	public void next(View view){
		showNext();
	}
	/**
	 * 上一步
	 * @param view
	 */
	public void pre(View view){
		showPre();
	}
	public boolean onTouchEvent(MotionEvent event) {
		detector.onTouchEvent(event);
		return super.onTouchEvent(event);
	}
}
